async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { statuspekerja, e } = req?.body;
  console.log(statuspekerja);
  let res = "Failure";

  if (e === "ReadAllData") {
    const readStatuspekerja = await db.status_Pekerja.findMany({
      orderBy: {
        id: "asc",
      },
    });
    statuspekerja = readStatuspekerja;
    // console.log(barang);
    res = "Read barang Success";
  }
  if (e === "ReadById") {
    const readStatuspekerja = await db.status_Pekerja.findFirst({
      where: {
        id: statuspekerja.id,
      },
    });
    statuspekerja = readStatuspekerja;
    res = statuspekerja
      ? "Read statuspekerja Success"
      : "statuspekerja not found";
  }
  if (e === "Delete") {
    try {
      statuspekerja = await db.status_Pekerja.delete({
        where: {
          id: statuspekerja.id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "Searching") {
    const key = "%" + statuspekerja.status_pekerja + "%";
    const readStatuspekerja = await db.status_Pekerja.findMany({
      where: {
        status_pekerja: {
          contains: key,
          mode: "insensitive",
        },
      },
    });
    statuspekerja = readStatuspekerja;
    // console.log(authors);
    res = "Read nama_departemen Success";
  }
  reply.send({
    res: res,
    statuspekerja,
    e,
  })
}
