async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.kota.findMany();
    reply.send({
      data: data,
      user: user,
    });
  };

  const postdata = async () => {
    const validatedata = await checkvalidasikota(req.body);
    console.log(validatedata);
    if (validatedata) {
      try {
        const alamat = await db.kota.create({
          data: {
            nama_kota: req.body.nama_kota,
          },
        });
        reply.send({
          data: alamat,
          status: "success created",
        });
      } catch (error) {
        reply.send({
          status: "error to send data",
        });
      }
    } else {
      reply.send({
        validatedata,
      });
    }
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
