content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.kota.findMany();
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const alamat = await db.kota.create({
      data: { nama_kota: req.body.nama_kota },
    });
    reply.send({
      data: alamat,
      status: "success created",
    });
  };

  const schemavalidate = async () => {};

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
