content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.status_Pekerja.findMany();
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    try {
      const alamat = await db.status_Pekerja.create({
        data: { status: req.body.status },
      });
      reply.send({
        data: alamat,
        status: "success created",
      });
    } catch (error) {
      reply.send({
        status: "error",
      });
    }
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
