async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { departemen, e } = req?.body;
  console.log(departemen);
  let res = "Failure";

  if (e === "ReadAllData") {
    const readDepartemen = await db.departemen.findMany({
      orderBy: {
        id: "asc",
      },
      include: {
        Alamat: true,
      },
    });
    departemen = readDepartemen;
    // console.log(barang);
    res = "Read barang Success";
  }
  if (e === "ReadById") {
    const readDepartemen = await db.departemen.findFirst({
      where: {
        id: departemen.id,
      },
    });
    departemen = readDepartemen;
    // console.log(user.barang.name);
    res = departemen ? "Read barang Success" : "barang not found";
  }
  if (e === "Delete") {
    try {
      departemen = await db.departemen.delete({
        where: {
          id: departemen.id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "Searching") {
    const key = "%" + departemen.nama_departemen + "%";
    const readDepartemen = await db.departemen.findMany({
      where: {
        nama_departemen: {
          contains: key,
          mode: "insensitive",
        },
      },
      include: {
        Alamat: true,
      },
    });
    departemen = readDepartemen;
    // console.log(authors);
    res = "Read nama_departemen Success";
  }
  reply.send({
    res: res,
    departemen,
    e,
  })
}
