async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.status_Pekerja.findMany();
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    console.log(req.body);

    const schema = Joi.object({
      status_pekerja: Joi.string().min(3).max(30).required(),
    });

    const validatorjoi = await schema.validate(req.body);

    console.log(validatorjoi);
    if (typeof validatorjoi.error === "undefined") {
      try {
        const data = await db.status_Pekerja.create({
          data: req.body,
        });
        reply.send({
          data: data,
          status: "success created",
        });
      } catch (error) {
        reply.send({
          status: "error to send data",
        });
      }
    } else {
      reply.send(validatorjoi.error);
    }
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
