content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.alamat_Departemen.findMany({
      include: {
        Kota: true,
        Provinsi: true,
      },
    });
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const schema = Joi.object({
      alamat: Joi.string().min(3).max(255).required(),
      kotaId: Joi.number().required(),
      provinsiId: Joi.number().required(),
    });

    const validator = schema.validate(req.body);
    if (typeof validator.error === "undefined") {
      const data = await db.alamat_Departemen.create({
        data: {
          alamat: req.body.alamat,
          kotaId: parseInt(req.body.kotaId),
          provinsiId: parseInt(req.body.provinsiId),
        },
      });
      reply.send({
        data: data,
        status: "success created",
      });
    } else {
      reply.send(validator.error);
    }
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
