content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.alamat_Departemen.findMany({
      include: {
        Kota: true,
        Provinsi: true,
      },
    });
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const schema = ext.Joi.object({
      alamat: ext.Joi.string().min(3).max(255).required(),
      kotaId: ext.Joi.number().required(),
      provinsiId: ext.Joi.number().required(),
    });

    const validator = await schema.validate(req.body);
    if (typeof validator.error === "undefined") {
      try {
        const data = await db.alamat_Departemen.create({
          data: {
            alamat: req.body.alamat,
            kotaId: parseInt(req.body.kotaId),
            provinsiId: parseInt(req.body.provinsiId),
          },
        });
        reply.send({
          data: data,
          status: "success created",
        });
      } catch (err) {
        reply.send("error sending data");
      }
    } else {
      reply.send(validator.error);
    }
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
