content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.lowongan_Kerja.findMany({
      where: {
        status_lowongan: "masih buka",
        Status_Pekerja: {
          status_pekerja: "internship",
        },
      },
      include: {
        Status_Pekerja: true,
        Kebutuhan_loker: true,
      },
    });
    reply.send({
      data: data,
    });
  };

  getdata()
}
