content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async (iddata) => {
    const data = await db.departemen.findUnique({
      where: {
        id: parseInt(iddata),
      },
      include: {
        Alamat: true,
      },
    });
    reply.send({
      data: data,
    });
  };

  const postdata = async (iddata) => {
    const alamat = await db.departemen.update({
      where: {
        id: Number(iddata),
      },
      data: {
        nama_departemen: req.body.nama_departemen,
        alamatId: Number(req.body.alamatId),
      },
    });
    reply.send({
      data: alamat,
      status: "success updated",
    });
  };

  const deletedata = async (iddata) => {
    const alamat = await db.departemen.delete({
      where: {
        id: Number(iddata),
      },
    });
    reply.send({
      data: alamat,
      status: "success deleted",
    });
  };

  switch (req.method) {
    case "GET":
      getdata(params.id);
      break;
    case "POST":
      postdata(params.id);
      break;
    case "DELETE":
      deletedata(params.id);
      break;
  }
}
