content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const data1 = await db.lowongan_Kerja.count({
    where: {
      status_lowongan: "masih buka",
    },
  });
  const data2 = await db.user.count({
    where: {
      role: "verified",
    },
  });
  const data3 = await db.proses_Apply.count({
    where: {
      status: "proses",
    },
  });
  const data4 = await db.departemen.count();
  const data5 = await db.proses_Apply.findMany({
    include: {
      User: {},
      Lowongan_Kerja: {},
      Departemen: {},
    },
    orderBy: {
      createdAt: "desc",
    },
  });

  reply.send({
    lowongan_kerja_count: data1,
    user_count: data2,
    pelamar_count: data3,
    departemen_count: data4,
    user: data5,
  })
}
