content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.departemen.findMany({
      include: {
        Alamat: true,
      },
    });
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const alamat = await db.departemen.create({
      data: {
        nama_departemen: req.body.nama_departemen,
        alamatId: Number(req.body.alamatId),
      },
    });
    reply.send({
      data: alamat,
      status: "success created",
    });
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
