async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const postdata = async () => {
    const schema = await ext.Joi.object({
      email: ext.Joi.string()
        .email({
          minDomainSegments: 2,
          tlds: { allow: ["com", "net"] },
        })
        .required(),
      pass: ext.Joi.string().required(),
    });

    const sendverifemail = (id, email) => {
      function getRndInteger(min, max) {
        return Math.floor(Math.random() * (max - min)) + min;
      }
      const uniquestring = getRndInteger(1000, 9000);
      const hashemailcode = ext.bcrypt.hashSync(`${uniquestring}`, 10);
      let info = ext.transporter.sendMail({
        from: '"Team 4 Andromedia ðŸ‘»" <andromediarecruitment@gmail.com>',
        to: email,
        subject: "âœ” Email Verifier âœ”",
        html: `this is your unique code  ${uniquestring}`,
      });

      return hashemailcode;
    };

    try {
      const { error, value } = schema.validate(req.body);
      // const validator = await schema.validate();

      if (typeof error === "undefined") {
        const datauser = await db.user.findUnique({
          where: { email: req.body.email },
        });
        if (!datauser) {
          const dataencrypt = await ext.bcrypt.hashSync(req.body.pass, 8);
          try {
            const data = await db.user.create({
              data: {
                email: req.body.email,
                pass: dataencrypt,
              },
            });

            await reply.send({
              data: data,
              status: "success created",
            });

            try {
              const emailverif = sendverifemail(data.id, data.email);

              const user = await db.user.update({
                where: {
                  id: Number(data.id),
                },
                data: {
                  email: req.body.email,
                  pass: dataencrypt,
                  email_verif: emailverif,
                },
              });
            } catch (err) {
              console.log("emailerror", err);
            }
          } catch {
            reply.send({
              status: "error connection to db",
            });
          }
        } else {
          reply.send({ status: "email taken" });
        }
      } else {
        reply.send(error);
      }
    } catch (err) {
      reply.send("validation undefined");
    }
  };

  switch (req.method) {
    case "POST":
      postdata();
      break;
  }
}
