content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.lowongan_kerja.findMany();
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const alamat = await db.lowongan_kerja.create({
      data: req.body,
    });
    reply.send({
      data: alamat,
      status: "success created",
    });
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
