content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const postdata = async () => {
    const schema = ext.Joi.object({
      email: ext.Joi.string()
        .email({
          minDomainSegments: 2,
          tlds: { allow: ["com", "net"] },
        })
        .required(),
      password: ext.Joi.string().required(),
      username: ext.Joi.string().required(),
    });

    const validator = await schema.validate(req.body);

    if (typeof validator.error === "undefined") {
      const dataemail = await db.admin.findUnique({
        where: { email: req.body.email },
      });
      if (!dataemail) {
        const datausername = await db.admin.findUnique({
          where: { username: req.body.username },
        });
        if (!datausername) {
          var dataencrypt = await ext.bcrypt.hashSync(req.body.password, 8);
          try {
            const data = await db.admin.create({
              data: {
                email: req.body.email,
                username: req.body.username,
                password: dataencrypt,
              },
            });
            reply.send({
              data: data,
              status: "success created",
            });
          } catch {
            reply.send({
              status: "error connection to db",
            });
          }
        } else {
          reply.send({ status: "username taken" });
        }
      } else {
        reply.send({ status: "email taken" });
      }
    } else {
      reply.send(validator.error);
    }
  };

  switch (req.method) {
    case "POST":
      postdata();
      break;
  }
}
