async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async (iddata) => {
    try {
      const data = await db.user.findUnique({
        where: {
          id: parseInt(iddata),
        },
        include: {
          experience: true,
          Pendidikan_User: true,
          Sertifikasi: true,
        },
      });
      reply.send({
        data: data,
      });
    } catch (e) {
      reply.send("not found").code(404);
    }
  };

  const postdata = async (iddata) => {
    var dataencrypt = await bcrypt.hashSync(req.body.pass, 8);
    const user = await db.user.update({
      where: {
        id: Number(iddata),
      },
      data: {
        email: req.body.email,
        pass: dataencrypt,
      },
    });
    reply.send({
      data: user,
      status: "success updated",
    });
  };

  const deletedata = async (iddata) => {
    const alamat = await db.user.delete({
      where: {
        id: Number(iddata),
      },
    });
    reply.send({
      data: alamat,
      status: "success deleted",
    });
  };

  switch (req.method) {
    case "GET":
      getdata(params.id);
      break;
    case "POST":
      postdata(params.id);
      break;
    case "DELETE":
      deletedata(params.id);
      break;
  }
}
