content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async (iddata) => {
    try {
      const data = await db.sertifikasi.findUnique({
        where: {
          id: parseInt(iddata),
        },
      });
      reply.send({
        data: data,
      });
    } catch (e) {
      reply.send().code(404);
    }
  };

  const postdata = async (iddata) => {
    const schema = ext.Joi.object({
      sertif: ext.Joi.string().min(3).max(255).required(),
      pembeda: ext.Joi.string().min(3).max(255).required(),
    });

    const validator = await schema.validate(req.body);
    if (typeof validator.error === "undefined") {
      try {
        const data = await db.sertifikasi.update({
          where: {
            id: Number(iddata),
          },
          data: req.body,
        });
        reply.send({
          data: data,
          status: "success updated",
        });
      } catch (error) {
        reply.send({
          status: "error to send data",
        });
      }
    } else {
      reply.send(validator.error);
    }
  };

  const deletedata = async (iddata) => {
    const data = await db.sertifikasi.delete({
      where: {
        id: Number(iddata),
      },
    });
    reply.send({
      data: data,
      status: "success deleted",
    });
  };

  switch (req.method) {
    case "GET":
      getdata(params.id);
      break;
    case "POST":
      postdata(params.id);
      break;
    case "DELETE":
      deletedata(params.id);
      break;
  }
}
