content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.alamat.findMany();
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const alamat = await db.alamat.create({
      data: {
        alamat: req.body.alamat,
        kotaId: Number(req.body.kotaId),
        provinsiId: Number(req.body.provinsiId),
      },
    });
    reply.send({
      data: alamat,
      status: "success created",
    });
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
