content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.departemen.findMany({
      include: {
        Alamat: true,
      },
    });
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const schema = ext.Joi.object({
      nama_departemen: ext.Joi.string().min(3).max(255).required(),
      alamatId: ext.Joi.number().required(),
    });

    const validator = await schema.validate(req.body);
    if (typeof validator.error === "undefined") {
      try {
        const data = await db.departemen.create({
          data: {
            nama_departemen: req.body.nama_departemen,
            alamatId: parseInt(req.body.alamatId),
          },
        });
        reply.send({
          data: data,
          status: "success created",
        });
      } catch (error) {
        reply.send({
          status: "error to send data",
        });
      }
    } else {
      reply.send(validator.error);
    }
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
