content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const getdata = async () => {
    const data = await db.lowongan_Kerja.findMany({
      include: {
        Status_Pekerja: true,
        Kebutuhan_loker: true,
      },
    });
    reply.send({
      data: data,
    });
  };

  const postdata = async () => {
    const schema = Joi.object({
      status_loker: Joi.string().alphanum().min(3).max(255).required(),
      info: Joi.string().alphanum().min(3).max(255).required(),
      start_open: Joi.required(),
      jumlah_diterima: Joi.number().required(),
      end_open: Joi.required(),
      status_lowongan: Joi.string().alphanum().min(3).max(255).required(),
    });

    const validator = schema.validate(req.body);
    if (typeof validator.error === "undefined") {
      try {
        const data = await db.lowongan_Kerja.create({
          data: req.body,
        });
        reply.send({
          data: data,
          status: "success created",
        });
      } catch (error) {
        reply.send({
          status: "error to send data",
        });
      }
    } else {
      reply.send(validator.error);
    }
  };

  switch (req.method) {
    case "GET":
      getdata();
      break;
    case "POST":
      postdata();
      break;
  }
}
